package com.test.practice;

public class Forloop50even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j;
		int sum1 =0;{

		for (j =0; j <=50 ; j++) {
			if(j % 2 == 0) {
			System.out.println(j);
			sum1 = sum1 + j;
			}
			System.out.println(sum1);
	}

}
}
}